% Calculate intensities, mean clients and average service time for given network.clc;clear all;close all;pkg load queueing;function [rho, ergodic] = intensities(lambda, mu)  rho(1) = lambda(1)/mu(1);  rho(2) = (lambda(2) + (2/7)*lambda(1))/mu(2);  rho(3) = ((4/7)*lambda(1))/mu(3);  rho(4) = ((3/7)*lambda(1))/mu(4);  rho(5) = (lambda(2) + (4/7)*lambda(1))/mu(5);  ergodic = 1;  for i=1:5    if rho(i) >= 1      ergodic = 0;    endif    printf("Intensity of Queue %d is equal to: %d.\n", i, rho(i));  endforendfunctionfunction [q] = mean_clients(lambda, mu)  [rho, ergodic] = intensities(lambda, mu);  q = rho ./ (1 - rho);endfunctionlambda = [4, 1];mu = [6, 5, 8, 7, 6];q = mean_clients(lambda, mu);et = sum(q)/sum(lambda);printf("Mean service time is equal to: %d.\n", et);[maxq, maxi] = max(q);max_lambda = 1 * mu(maxi);printf('The maximum value lambda_1 can have is: %d.\n', max_lambda);for i=1:9  x(i) = NaN;  et2(i) = NaN;endforfor i=10:99  l = max_lambda*i/100;  x(i) = l;  lambda(1) = l;  et2(i) = sum(mean_clients(lambda, mu))/sum(lambda);endforfigure(1);plot(x, et2);title('Mean Service Time as a function of \lambda_1');xlabel('\lambda_1');ylabel('Service Time');